
public class Concate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] a={1,2,3};
		System.out.println(a.length);
		
		if(a.length==1)
		
			System.out.println(a[0]);	
		
		else
			if(a.length==2)
				
				System.out.println(a[1]);
		
		else
		
			if(a.length==3)
			{
				int k=a[1]+a[2];
				
				System.out.println(k);
						
						
		}
			
		
	}

}
